INSERT INTO `news` (`id`, `status`, `position`, `title`, `date`, `intro`, `img`, `content`, `alias`, `meta_title`, `meta_keywords`, `meta_description`, `created_at`, `updated_at`) VALUES
(1, 10, 10, 'Требуется менеджер по продажам абонементов', '2019-04-17', 'Уважаемые кандидаты! Мы рассматриваем резюме с фотографиями!', '', '<p>Обязанности:</p>\r\n\r\n<p>Презентация клуба<br />\r\nПродажа и оформление клубных карт<br />\r\nПрием входящих звонков<br />\r\nИсходящие звонки по внутренней базе (не холодные звонки!)<br />\r\nРабота с первичной документацией в 1С<br />\r\nРабота с возражениями<br />\r\nАдминистративной работы нет, Вы будете заниматься только продажами</p>\r\n\r\n<p>Требования:</p>\r\n\r\n<p>Опыт продаж от 1 года<br />\r\nВладение техникой продаж<br />\r\nОтличное знание ПК<br />\r\nКоммуникабельность и обучаемость<br />\r\nГрамотная речь<br />\r\nНацеленность на результат</p>\r\n\r\n<p>Условия:</p>\r\n\r\n<p>Дружный коллектив<br />\r\nПросьба обратить внимание, что окладная часть отсутствует, заработная строится от индивидуальных продаж менеджера (от процента)<br />\r\nПри выполнении плана з/п от 40 000р<br />\r\nКарьерный рост<br />\r\nБесплатное корпоративное обучение<br />\r\nОформление по ТК РФ</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Работа по адресу ТРК &quot;ИЮНЬ&quot;,&nbsp;Индустриальный просп., 24, Санкт-Петербург<br />\r\nГрафик работы 2/2 с 10:00 до 22:00<br />\r\nБесплатная клубная карта в фитнес клуб</p>\r\n\r\n<p>По всем вопросам звоните:&nbsp;8(999)209-35-79 (Антон)&nbsp;</p>\r\n\r\n<p><br />\r\nПочта: Hr.extrasport@yandex</p>\r\n', 'trebuetsa-menedzer-po-prodazam-abonementov', 'Требуется менеджер по продажам абонементов', '', '', '2019-04-15 08:01:46', '2019-04-25 15:46:31'),
