INSERT INTO `news` (`id`, `status`, `position`, `title`, `date`, `intro`, `img`, `content`, `alias`, `meta_title`, `meta_keywords`, `meta_description`, `created_at`, `updated_at`) VALUES
(1, 10, 10, 'Extra Sport поздравляет всех с наступающими праздниками', '2019-04-17', 'Также, информируем о работе клубов в праздничные дни', '', '<p>Дорогие друзья!&nbsp;<br />\r\nСеть фитнес-клубов ExtraSport поздравляет всех с наступающим Новым годом и Рождеством! Желаем вам счастья, здоровья и сил, чтобы легко добиваться поставленных целей!&nbsp;</p>\r\n\r\n<p>Также, информируем о работе клубов в праздничные дни:<br />\r\n<br />\r\nТК &quot;Южный полюс&quot;:&nbsp;<br />\r\n31 декабря - 9.00 - 15.00&nbsp;<br />\r\n1 и 2 января - выходной&nbsp;<br />\r\n3 - 8 января - 9.00 - 22.00</p>\r\n', 'extra-sport-pozdravlaet-vseh-s-nastupausimi-prazdnikami', 'Extra Sport поздравляет всех с наступающими праздниками', '', '', '2019-04-15 08:01:46', '2019-04-25 15:54:11'),
